import React, { useState } from 'react'
import sportData from "./Sports"
import axios from "axios"
import { Select,Textarea,Input } from '@mui/joy';
// import { Input as FileInput } from '@mui/material';
import './App.css';


const postURL = "http://localhost:7000/form"
function App() {
  const [input, setInput] = useState({});
  const [fee, setFee] = useState(0)

  console.log(input, fee)

  const handleInput = (e) => {
    const name = e.target.name
    const value = e.target.value

    setInput({ ...input, [name]: value })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    axios.post(postURL, input)
      .then(res => { console.log("posted successfully", res) })
      .catch(err => { console.log("error", err.message) })
  }
  const conversion = (file) => {
    // setInput({...input ,[file.name]:file.files[0]})
    toBase64(file.files[0]).then(res => {
      setInput({ ...input, [file.name]: res })
    }).catch(err => {
      console.log(`error ${err.message}`)
    })
  }
  const toBase64 = file => new Promise((resolve, reject) => {
    const reader = new FileReader()
    reader.readAsDataURL(file)
    reader.onload = () => resolve(reader.result)
    reader.onerror = (error) => reject(error)
  })
  return (
    <div className='form'>
      <form>
        <p>College Name:</p>
        <Input type="text" name='college_name' onChange={handleInput} />
        <p>College Address:</p>
        <Textarea minRows={3} placeholder="location..." size="sm" name='college_location' onChange={handleInput} />
        <p> Captains Phone number</p>
        <Input type="text" name='captains_Phone_no' onChange={handleInput} />

        <p>Captains College Id card:</p>
        <input variant="plain" type="file" name='id_card' onChange={(e) => { conversion(e.target) }} />
        <p>Payment Screenshot:</p>
        <input variant="plain" type="file" name='payment_screenshot' onChange={(e) => { conversion(e.target) }} />
        < button variant="contained" type='submit' onClick={handleSubmit}>submit</ button>
      </form>
    </div>
  );
}

export default App;
