import React, { useState } from 'react'

function Form() {
    const [inputs, setInputs] = useState({});
    const [selected, setSelected] = useState()
    const handleChange = (event) => {
        event.preventDefault();

        const name = event.target.name;
        const value = event.target.value;

        setInputs(values => ({ ...values, [name]: value  }))
        // console.log(inputs)
        // console.log(event)

    }

    const handlechange = (event)=>{
        event.preventDefault();
        const name = event.target.name;
        const value = event.target.value;

        setInputs(values => ({ ...values, value  }))
        console.log(inputs);
        // setSelected(event.target.value);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(event.target.value)
        // alert(inputs);
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>Enter your name:
                <input
                    type="text"
                    name="username"
                    value={inputs.username || ""}
                    onChange={handleChange}
                />
            </label>
            <br />
            <label>Enter your age:
                <input
                    type="number"
                    name="age"
                    value={inputs.age || ""}
                    onChange={handleChange}
                />
            </label>
            <br />
            <label for="cars">Choose a Sport:</label>
            <select id="cars" name="sport"onChange={handlechange}>
            <option value="" selected disabled hidden>Choose here</option>
                <option key ="Football"  value="Football">Football</option>
                <option key ="Cricket"  value="Cricket">Cricket</option>
                <option key ="Chess"  value="Chess">Chess</option>

            </select>
            <input type="submit" />
        </form>
    )
}

export default Form
